class Loader {
    constructor(container) {
        this.container = container;
		jQuery(document).ready(function () {
			 jQuery(this.container).append(`
                            <script src="https://unpkg.com/@dotlottie/player-component@latest/dist/dotlottie-player.mjs" type="module"></script> 
                            <dotlottie-player src="https://lottie.host/264201f6-19c0-4e4e-82a7-775063a04e45/sgBK12Iu2c.json" background="transparent" speed="1" style="width: 250px;" loop autoplay id="lottie-loader-animation"></dotlottie-player> `);
			})
       
    }

    displayLoader() {
        jQuery("#container-petbuy-menu").css("display", "none");
        jQuery(this.container).removeClass("loader-hidden");
        jQuery(this.container).addClass("loader-visible");
    }

    hideLoader() {
        jQuery(this.container).removeClass("loader-visible");
        jQuery(this.container).addClass("loader-hidden");
        jQuery("#container-petbuy-menu").css("display",);
    }

}

