function initLoader(j)
{
	j("body").prepend("<div class='loader-pt'></div>")
}

function showLoader(j)
{
	j(".loader-pt").append("<span class='loader-inner'></span>")
	j(".loader-pt").addClass("loader-pt-visible")
}

function removeLoader(j)
{
	j(".loader-pt").removeClass("loader-pt-visible")
	j(".loader-inner").remove()
}

jQuery(document).ready(function( $ ){
    
	initLoader($);
	
	$(document).on("click", ".header-wishlist", function (){
		
		$(this).attr("href", null);
		showLoader($);
		
		if ($('#woosw_wishlist').hasClass('woosw-loaded')) 
			woosw_wishlist_show();
		else 
			woosw_wishlist_load();
    });
});

