const animationTime = 600;
const showContentTime = 60000;

function drawNewBanner(q, elm, html)
{
	q(elm).prepend(html);
	setTimeout(() => {	
		q(elm +  " > .banner-content").removeClass("animation-in")
	}, animationTime);
}

function showBannerAfter(u, elm, time)
{
	setTimeout(() => {
		u(elm + " > *").addClass("animation-out");
		setTimeout(() => {
			u(".banner-content").remove();
			u(elm + " > *").removeClass("animation-out")
			u(elm + " > *").addClass("animation-in")
			u(elm + " > *").removeClass("hide-banner");
			setTimeout(() => {
				u(elm + " > *").removeClass("animation-in")
			}, animationTime);
    	}, animationTime);
    }, time);
}

function hideBanner(j, el)
{
	showBannerAfter(j, el, showContentTime);
	j(el + " > *").addClass("animation-out");
	setTimeout(() => {
		j(el + " > *").addClass("hide-banner");
		drawNewBanner(j, el, `<p class='banner-content animation-in'>
								  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. </p>`)
		j(el + " > *").removeClass("animation-out");
    }, animationTime - 100);
}

jQuery(document).ready(function( $ ){
    $(document).on("click", ".link", function(){
		const ele = $($(this)).html();
		const id = ele.substring(25, 26);
		if (id == 1)  // banner informativo 1
			hideBanner($, "#banner-1")
		if (id == 2)  // banner informativo 2
			hideBanner($, "#banner-2")
		if (id == 3)  // banner informativo 3
			hideBanner($, "#banner-3")
	});
});

