function initSwiper()
{
	const swiper = new Swiper('.swiper', {
		clickable:  true,
		spaceBetween : 30,
		centeredSlides : true,
		direction: "horizontal",
		pagination: {
			el: ".swiper-pagination",
			clickable: true,
		},
		autoplay : {
			delay : 10000,
			disableOnInteraction: false,
		}
	})
	return swiper;
}

jQuery(document).ready(function ($) {
	
	if (location.href != "http://petbuy.local/")
		return;
	
	swiper = initSwiper()
	
    swiper.on('slideChange', function () {
        if (this.realIndex == 0) //basicUser
        {
            $("#business").removeClass("animation-top-bottom");
            $("#basic").addClass("animation-bottom-top");
        }
        else if (this.realIndex == 1) //businessUser
        {
            $("#basic").removeClass("animation-bottom-top");
            $("#business").addClass("animation-top-bottom");
        }
    });
});

